import React, {Component } from 'react';
import CardList from '../components/CardList';
import Scroll from '../components/Scroll';
import Searchbox from '../components/Searchbox';
import ErrorBoundary from '../components/ErrorBoundary';
import { setSearchField } from '../actions';
import {connect} from 'react-redux';

const mapStateToProps = state => {
  return {
    searchField: state.searchRobots.searchField,
    robots: state.requestRobots.robots,
    isPending: state.REQUEST_ROBOTS_PENDING.isPending,
    error: state.REQUEST_ROBOTS_FAILED.error
  }
}

const mapDispatchToProps = (dispatch) => {
  return {
    onSearch: (event) => dispatch(setSearchField(event.target.value))

  }
}



class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
      robots: [],
      searchfield: ''
    }
  }

  componentDidMount() {
      fetch('https://jsonplaceholder.typicode.com/users')
      .then(response => response.json())
      .then(users => this.setState({
        robots: users
      }))

  }

  render() {
    const { robots } = this.state;
    const {searchField, onSearch } = this.props;
    const filterRobots = robots.filter(robot => {
      return robot.name.toLowerCase().includes(searchField.toLowerCase());
    })

    return ( <
      div className = "tc" >
      <h1 className = "header tc f1" > Robot Buddies < /h1>
      <Searchbox searchChange = {onSearch}/>
      < Scroll >
      <ErrorBoundary>
      <CardList robots = {filterRobots} />
      </ErrorBoundary>
      </Scroll>
      </div>
    );
  }setSearchField
}
export default connect(mapStateToProps, mapDispatchToProps)(App);
